void print_decoder(LDPC_decoder dec)
{
	for (int i = 0; i < dec.cLen; i++) 
	{
		printf("\nCheckNode: %d", dec.check[i]->name);
		for (int j = 0; j < dec.check[i]->connectedNodes; j++)
		{
			printf("\n\t %d", dec.check[i]->varNode[j]->name);
		}
	}
	printf("\n");
	for (int i = 0; i < dec.vLen; i++) 
	{
		printf("\n VarNode: %d", dec.var[i]->name);
		for (int j = 0; j < dec.var[i]->connectedNodes; j++)
		{
			printf("\n\t %d", dec.var[i]->chNode[j]->name);
		}
	}
	printf("\n");
}


LDPC_decoder construct_decoder(int length, unsigned int sparse[2][length])
{
	//Step 0. Find minima and maxima of sparse, initialize checkNodes and varNodes
	//printf("Step 0 Started \n");
	int cols = 0;
	int rows = 0;
	for (int i = 0; i < length; i++)
	{
		
		if (sparse[0][i] > cols) cols = sparse[0][i];
		if (sparse[1][i] > rows) rows = sparse[1][i];
	}
	cols++;
	rows++;
	
	int checkConnections[rows];
	int varConnections[cols]; 
	
	for (int i = 0; i < rows; i++) checkConnections[i] = 0;
	for (int i = 0; i < cols; i++) varConnections[i] = 0; 
	
	
	for (int i = 0; i < length; i++)
	{
		checkConnections[sparse[1][i]]++;
		varConnections[sparse[0][i]]++;
	}
		//Initialize and malloc structs
	checkNode * check[rows];
	variableNode * var[cols];
	
	for (int i = 0; i < rows; i++)
	{
		check[i] = (checkNode *) malloc(sizeof(checkNode));
		check[i]->index = (int *) malloc(sizeof(int)*checkConnections[i]);
		check[i]->messages = (double *) malloc(sizeof(double)*checkConnections[i]);
		check[i]->varNode = (variableNode **) malloc(sizeof(variableNode*)*checkConnections[i]);
		check[i]->connectedNodes = 0;
		check[i]->name = i;
		//printf("%d ", i);
		
	}	
	//printf("\n");


	for (int i = 0; i < cols; i++)
	{
		var[i] = (variableNode *) malloc(sizeof(variableNode));
		var[i]->index = (int *) malloc(sizeof(int)*varConnections[i]);
		var[i]->messages = (double *) malloc(sizeof(double)*varConnections[i]);
		var[i]->chNode = (checkNode **) malloc(sizeof(checkNode*)*varConnections[i]);
		var[i]->connectedNodes = 0;
		var[i]->name = i;
		//printf("%d ", i);
	}
	//Step 1. Loop through sparse matrix and interconnect nodes
	int j, k;
	//for (int i = 0; i < rows; i++) printf("%d : %d \n", i, checkConnections[i]);
	//printf("\n var :");
	//for (int i = 0; i < cols; i++) printf("%d : %d \n", i, varConnections[i]);
	for (int i = 0; i < length; i++) 
	{
		//
		
		j = sparse[1][i];
		k = sparse[0][i];
		//printf("A bit underway, %d %d %d\n", i, j, k);
		//printf("1: %d\n", var[k]->connectedNodes);
		check[j]->varNode[check[j]->connectedNodes] = var[k];
		//printf("2: %d\n", var[k]->connectedNodes);
		check[j]->connectedNodes++;
		//printf("3: %d\n", var[k]->connectedNodes);
		var[k]->chNode[var[k]->connectedNodes] = check[j];
		//printf("4: %d\n", var[k]->connectedNodes);
		var[k]->connectedNodes++;		
	}
	
	//Check if all nodes have been connected correctly
	for (int i = 0; i < cols; i++)
	{
		if(var[i]->connectedNodes != varConnections[i]) printf("Error: issue with node %d\n", i);
	}
	for (int i = 0; i < rows; i++)
	{
		if(check[i]->connectedNodes != checkConnections[i]) printf("Error: issue with node %d\n", i);
	}
	
	//Step 2. Create decoder and return it
	LDPC_decoder dec;
	//Create attachment points for checkNodes and varNodes
	dec.check = (checkNode **) malloc(sizeof(checkNode*)*rows);
	dec.var = (variableNode **) malloc(sizeof(variableNode*)*cols);
	
	//attach checkNodes and varNodes
	for (int i = 0; i < rows; i++) dec.check[i] = check[i];
	for (int i = 0; i < cols; i++) dec.var[i] = var[i];
	dec.cLen = rows;
	dec.vLen = cols;
	return dec;
}

void destruct_decoder(LDPC_decoder dec)
{
	//Step 1. Release variables inside structs
	for (int i = 0; i < dec.cLen; i++)
	{
		free(dec.check[i]->index);
		free(dec.check[i]->messages);
		free(dec.check[i]->varNode);
	}
	for (int i = 0; i < dec.vLen; i++)
	{
		free(dec.var[i]->index);
		free(dec.var[i]->messages);
		free(dec.var[i]->chNode);
	}
	//Step 2. Release structs
	free(dec.var);
	free(dec.check);
	//Step 3. Release reference to structs	
	return;
}

int main() 
{
	LDPC_decoder dec = construct_decoder(2376, parity);
	print_decoder(dec);
	destruct_decoder(dec);
	return 0;
}

